import React from "react";
import "./Box.css";
import { motion } from "framer-motion/dist/framer-motion";

const Box = ({ innerref, name, icon, id }) => {
  return (
    <motion.div className="info" ref={innerref}>
      <img src={icon} alt="skill" key={id} />
      <span>{name}</span>
    </motion.div>
  );
};

export default Box;
